mapper.select:
SELECT sr.id as id, sr.type as type, sr.office_id as officeId, sr.name as name, sr.enname as enname, sr.role_type as roleType, sr.data_scope as dataScope, sr.is_sys AS isSys, sr.useable as useable , sr.create_by as createBy, sr.create_date as createDate, sr.update_by as updateBy, sr.update_date as updateDate, sr.remarks as remarks, sr.del_flag as delFlag, nc.compan_name as companName FROM sys_role sr left join nj_coporate nc on nc.id = sr.office_id where sr.del_flag=0
and (sr.name like "%"#{name,jdbcType=VARCHAR}"%" or sr.role_type like "%"#{name,jdbcType=VARCHAR}"%")
and nc.id = #{coporateId,jdbcType=VARCHAR}
and sr.type = #{type,jdbcType=VARCHAR}
order by sr.create_date desc limit #{start},#{length}

SELECT count(1) FROM sys_role sr left join nj_coporate nc on nc.id = sr.office_id where sr.del_flag=0
and (sr.name like "%"#{name,jdbcType=VARCHAR}"%" or sr.role_type like "%"#{name,jdbcType=VARCHAR}"%")
and nc.id = #{coporateId,jdbcType=VARCHAR}
and sr.type = #{type,jdbcType=VARCHAR}
