mapper.select:
select ur.*,ur.first_payment as firstPayment,ur.first_project_no as firstProjectNo,ur.last_project_no as lastProjectNo,ur.insurance_no as insuranceNO,ur.last_project_no as lastProjectNo ,ur.first_project_no as firstProjectNo from nj_order_user_car our LEFT JOIN nj_user_car ur 
	on our.user_car_id = ur.id 
	where our.order_id = #{orderId,jdbcType=VARCHAR}
